Wowser.ui.screens.World = (function() {
  function World($scope, $element) {
    var axes, grid;
    this.$scope = $scope;
    this.$element = $element;
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 1, 1000);
    this.camera.position.z = 500;
    this.camera.position.y = 500;
    this.camera.position.x = 500;
    this.controls = new THREE.OrbitControls(this.camera);
    this.controls.noKeys = true;
    this.controls.minPolarAngle = Math.PI * 0.05;
    this.controls.maxPolarAngle = Math.PI * 0.45;
    this.controls.minDistance = 200;
    this.controls.maxDistance = 600;
    this.renderer = new THREE.WebGLRenderer({
      canvas: this.$element[0]
    });
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    axes = new THREE.AxisHelper(150);
    axes.position.set(0, 0, 0);
    this.scene.add(axes);
    grid = new THREE.GridHelper(300, 10);
    grid.setColors(new THREE.Color(0x666666), new THREE.Color(0x222222));
    this.scene.add(grid);
    this.run();
  }

  World.prototype.run = function() {
    var _this = this;
    this.update();
    this.animate();
    return requestAnimationFrame(function() {
      return _this.run();
    });
  };

  World.prototype.update = function() {
    return this.controls.update();
  };

  World.prototype.animate = function() {
    return this.renderer.render(this.scene, this.camera);
  };

  return World;

})();
